def img_to_b64(path):
    """Return base64 string of image, fallback to default logos if path missing."""
    for p in [path, "assets/rentford_logo.jpg", "assets/brentford_logo.png"]:
        if os.path.exists(p):
            with open(p,"rb") as f:
                return base64.b64encode(f.read()).decode()
    return None


